<?phpif ($_SERVER['REQUEST_METHOD'] === 'POST') {    if (isset($_FILES['ddsfile']) && $_FILES['ddsfile']['error'] === UPLOAD_ERR_OK) {        $uploadDir = __DIR__ . '/uploads/';        $outputDir = __DIR__ . '/output/';        $templateTex = __DIR__ . '/templates/base.tex'; // template TEX yang valid        if (!is_dir($uploadDir)) mkdir($uploadDir, 0777, true);        if (!is_dir($outputDir)) mkdir($outputDir, 0777, true);        $uploadedDDS = $uploadDir . basename($_FILES['ddsfile']['name']);        move_uploaded_file($_FILES['ddsfile']['tmp_name'], $uploadedDDS);        $filenameNoExt = pathinfo($uploadedDDS, PATHINFO_FILENAME);        $generatedTex = $outputDir . $filenameNoExt . '.tex';        $outputPNG = $outputDir . $filenameNoExt . '.png';        if (!file_exists($templateTex)) {            die("Template TEX tidak ditemukan di $templateTex");        }        // Copy dulu template.tex ke file baru sebagai base        copy($templateTex, $generatedTex);        // Jalankan python untuk update TEX berdasarkan DDS        $pythonCmd = 'python'; // bisa diganti python3 kalau perlu        $cmd1 = "$pythonCmd aa.py -i \"$uploadedDDS\" -o \"$generatedTex\" -c";        $out1 = shell_exec($cmd1 . ' 2>&1');        if (!file_exists($generatedTex)) {            die("Gagal konversi DDS ke TEX.<br><pre>$out1</pre>");        }      }   }}?><!DOCTYPE html><html><head><title>Upload DDS</title></head><body><h1>Upload file DDS</h1><form method="post" enctype="multipart/form-data">    <input type="file" name="ddsfile" accept=".dds" required>    <button type="submit">Konversi</button></form></body></html>